import React from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion'; // Make sure Framer Motion is imported

function HeaderLinks() {
  const [isOpen, setIsOpen] = useState(false); // State to manage dropdown visibility
  // Define variants for the dropdown container to control its visibility
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1, // Delay between animations of child components
      },
    },
  };

  // Define variants for individual dropdown items
  const itemVariants = {
    hidden: { y: -20, opacity: 0 },
    visible: { y: 0, opacity: 1 },
  };

  return (
    <div className="dropdown">
      <button className="btn btn-secondary dropdown-toggle" type="button" id="dropdownMenuButton" data-bs-toggle="dropdown" aria-expanded="false">
        Menu
      </button>
      <motion.div className="dropdown-menu" aria-labelledby="dropdownMenuButton"
        variants={containerVariants}
        initial="hidden"
        animate="visible"
        // No need for 'exit' here since it's controlled by Bootstrap's toggle
      >
        {/* Apply the itemVariants to each dropdown item for the staggered animation */}
        <motion.div variants={itemVariants}><Link to="/" className="dropdown-item">Home</Link></motion.div>
        <motion.div variants={itemVariants}><Link to="/about" className="dropdown-item">About</Link></motion.div>
        <motion.div variants={itemVariants}><Link to="/contact" className="dropdown-item">Contact</Link></motion.div>
        <motion.div variants={itemVariants}><Link to="/signup" className="dropdown-item">Sign Up</Link></motion.div>
      </motion.div>
    </div>
  );
}

export default HeaderLinks;
