import React, { useState } from 'react';
import { motion } from 'framer-motion';
import HeaderLinks from './HeaderLinks';
import Logo from './assets/mgilogo.png';
import { Link } from 'react-router-dom';

function Header() {
  const [hoverItem, setHoverItem] = useState(null);

  // Motion variants for the dropdown animation
  const dropdownVariants = {
    hidden: { height: 0, opacity: 0 },
    visible: { height: '50vh', opacity: 1, transition: { duration: 0.5 } },
  };

  return (
    <div>
<nav className="navbar navbar-expand-lg navbar-light fixed-top" 
     style={{ backgroundColor: hoverItem ? '#ffffff' : 'transparent' }}>
  <Link to="/" className="navbar-brand">
    <img src={Logo} alt="Logo" style={{ height: '50px' }} />
    My Website
  </Link>
  <div className="container-fluid d-flex justify-content-center">
    <div className="navbar-nav">
      <HeaderLinks setHoverItem={setHoverItem} />
    </div>
  </div>
</nav>
      <motion.div
        className="position-fixed w-100"
        initial="hidden"
        animate={hoverItem ? "visible" : "hidden"}
        variants={dropdownVariants}
        style={{ top: 0, zIndex: -1 }} // Ensure this is behind the navbar content
      >
        {/* Dynamic content based on hoverItem */}
        {hoverItem && <p className="p-4">{hoverItem} content</p>}
      </motion.div>
    </div>
  );
}

export default Header;
