import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function HeaderLinks() {
  const [hoveredItem, setHoveredItem] = useState(null);

  const links = [
    { name: 'Home', path: '/', categories: [] },
    {
      name: 'Products',
      path: '/products',
      categories: [
        { name: 'Electronics', path: '/products/electronics' },
        { name: 'Furniture', path: '/products/furniture' },
      ],
    },
    { name: 'About', path: '/about', categories: [] },
    { name: 'Contact', path: '/contact', categories: [] },
  ];

  return (
    <div className="navbar-nav">
      {links.map(({ name, path, categories }) => (
        <div
          key={name}
          onMouseEnter={() => setHoveredItem(name)}
          onMouseLeave={() => setHoveredItem(null)}
          className={`nav-item ${categories.length > 0 ? 'dropdown' : ''}`}
        >
          <Link to={path} className="nav-link">
            {name}
          </Link>
          {categories.length > 0 && hoveredItem === name && (
            <div className="dropdown-menu show">
              {categories.map((category) => (
                <Link key={category.name} to={category.path} className="dropdown-item">
                  {category.name}
                </Link>
              ))}
            </div>
          )}
        </div>
      ))}
    </div>
  );
}

export default HeaderLinks;
