import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';

function HeaderLinks() {
  const [isOpen, setIsOpen] = useState(false); // State to manage dropdown visibility

  const containerVariants = {
    hidden: { opacity: 0, scale: 0.95 },
    visible: { opacity: 1, scale: 1, transition: { staggerChildren: 0.1 } },
    exit: { opacity: 0, scale: 0.95 },
  };

  const itemVariants = {
    hidden: { y: -20, opacity: 0 },
    visible: { y: 0, opacity: 1 },
    exit: { y: -20, opacity: 0 },
  };

  // Handlers for mouse enter and leave events
  const handleMouseEnter = () => setIsOpen(true);
  const handleMouseLeave = () => setIsOpen(false);

  return (
    <div className="dropdown" onMouseLeave={handleMouseLeave}>
      <span 
        className="dropdown-toggle" 
        onMouseEnter={handleMouseEnter} 
        style={{ cursor: 'pointer', color: '#007bff' }} // Style as needed
      >
        Words
      </span>
      <motion.div className={`dropdown-menu${isOpen ? ' show' : ''}`}
        variants={containerVariants}
        initial="hidden"
        animate={isOpen ? "visible" : "exit"}
      >
        <motion.div variants={itemVariants}><Link to="/" className="dropdown-item">Home</Link></motion.div>
        <motion.div variants={itemVariants}><Link to="/about" className="dropdown-item">About</Link></motion.div>
        <motion.div variants={itemVariants}><Link to="/contact" className="dropdown-item">Contact</Link></motion.div>
        <motion.div variants={itemVariants}><Link to="/signup" className="dropdown-item">Sign Up</Link></motion.div>
      </motion.div>
    </div>
  );
}

export default HeaderLinks;
