import React from 'react';
import { Link } from 'react-router-dom';

function HeaderLinks({ setHoverItem }) {
  const links = [
    { name: 'Home', path: '/', categories: [] },
    { name: 'Products', path: '/products', categories: [{ name: 'Electronics', path: '/products/electronics' }, { name: 'Furniture', path: '/products/furniture' }] },
    { name: 'About', path: '/about', categories: [] },
    { name: 'Contact', path: '/contact', categories: [] },
  ];

  // Local state for managing hover might still be needed depending on your UI logic
  // For simplicity, this example doesn't implement dynamic show/hide logic based on hover state

  return (
    <div className="navbar-nav">
      {links.map(({ name, path, categories }) => (
        <div key={name} className="nav-item">
          <Link to={path} className="nav-link">{name}</Link>
          {categories.length > 0 && (
            <div className="submenu"> {/* Placeholder for your submenu styling */}
              {categories.map(category => (
                <Link key={category.name} to={category.path} className="dropdown-item">
                  {category.name}
                </Link>
              ))}
            </div>
          )}
        </div>
      ))}
    </div>
  );
}


export default HeaderLinks;
