import React, { useState } from 'react';
import { motion } from 'framer-motion';
import HeaderLinks from './HeaderLinks';
import { Link } from 'react-router-dom';
import Logo from './assets/mgilogo.png';

function Header() {
  const [isHovering, setIsHovering] = useState(false);

  const headerVariants = {
    initial: {
      backgroundColor: 'rgba(255, 255, 255, 0)', // Transparent
    },
    hover: {
      backgroundColor: '#ffffff', // Solid color when hover
      transition: { duration: 0.5 },
    },
  };

  return (
    <motion.nav
      initial="initial"
      animate={isHovering ? 'hover' : 'initial'}
      variants={headerVariants}
      className="navbar navbar-expand-lg navbar-light fixed-top"
      style={{ zIndex: 1030 }}
      transition={{ type: 'spring', stiffness: 120 }}
    >
      <div className="container-fluid d-flex justify-content-between align-items-center">
        <Link to="/" className="navbar-brand">
          <img src={Logo} alt="Logo" style={{ height: '50px' }} />
        </Link>
        <div className="d-flex justify-content-center flex-grow-1">
          {/* Pass setIsHovering to HeaderLinks */}
          <HeaderLinks setIsHovering={setIsHovering} />
        </div>
        <div style={{ width: '50px' }}></div> {/* Placeholder for balance */}
      </div>
    </motion.nav>
  );
}

export default Header;
