import React from 'react';
import { Link } from 'react-router-dom';

function HeaderLinks({ setHoverItem, setCategories }) {
  const links = [
    { name: 'Home', path: '/', categories: [] }, // Assuming no categories for Home
    { 
      name: 'Products', 
      path: '/products',
      categories: [
        { name: 'Electronics', path: '/products/electronics' },
        { name: 'Furniture', path: '/products/furniture' },
        // Add more categories as needed
      ] 
    },
    { name: 'About', path: '/about', categories: [] }, // Assuming no categories for About
    { name: 'Contact', path: '/contact', categories: [] }, // Assuming no categories for Contact
  ];

  return (
    <div className="navbar-nav">
      {links.map(({ name, path, categories }) => (
        <div
          key={name}
          onMouseEnter={() => setHoverItem(name, categories)} // Pass categories on hover
          onMouseLeave={() => setHoverItem(null)}
          className="nav-item"
        >
          <Link to={path} className="nav-link">{name}</Link>
        </div>
      ))}
    </div>
  );
}

export default HeaderLinks;
