import React, { useState } from 'react';
import { motion } from 'framer-motion';
import HeaderLinks from './HeaderLinks';
import Logo from './assets/mgilogo.png';
import { Link } from 'react-router-dom';

function Header() {
  const [hoverItem, setHoverItem] = useState(null);
  const [isExpanded, setIsExpanded] = useState(false);

  // Defining the categories associated with each hoverItem
  const categories = {
    Products: [
      { name: 'Electronics', path: '/products/electronics' },
      { name: 'Furniture', path: '/products/furniture' },
    ],
    // Add other hoverItems and their categories as needed
  };

  const headerVariants = {
    initial: { height: "initial", backgroundColor: "transparent" },
    expanded: { height: "50vh", backgroundColor: "#ffffff", transition: { duration: 0.5 } },
  };

  return (
    <motion.div
      initial="initial"
      animate={isExpanded ? "expanded" : "initial"}
      variants={headerVariants}
      className="header-container"
      onMouseLeave={() => setIsExpanded(false)}
    >
      <nav className="navbar navbar-expand-lg navbar-light fixed-top">
        <div className="container-fluid d-flex justify-content-between">
          <Link to="/" className="navbar-brand">
            <img src={Logo} alt="Logo" style={{ height: '50px' }} />
          </Link>
          <HeaderLinks setHoverItem={(item) => { setHoverItem(item); setIsExpanded(!!item); }} />
        </div>
      </nav>
      {/* Expanded area for categories */}
      {isExpanded && hoverItem && (
        <div className="category-container d-flex justify-content-center" style={{ textAlign: 'center' }}>
          <ul style={{ listStyleType: 'none', padding: 0 }}>
            {categories[hoverItem]?.map(category => (
              <li key={category.name}><Link to={category.path} className="dropdown-item">{category.name}</Link></li>
            ))}
          </ul>
        </div>
      )}
    </motion.div>
  );
}

export default Header;
