// In Header.js, adjust the setHoverItem function and the rendering logic for the dropdown

function Header() {
  const [hoverItem, setHoverItem] = useState(null);
  const [categories, setCategories] = useState([]); // New state for categories

  // Adjust the expandVariants and the rest of your component as necessary...

  return (
    <div>
      <nav className="navbar navbar-expand-lg navbar-light fixed-top" 
           style={{ backgroundColor: hoverItem ? '#ffffff' : 'transparent' }}>
        <Link to="/" className="navbar-brand">
          <img src={Logo} alt="Logo" style={{ height: '50px' }} />
        </Link>
        <div className="container-fluid d-flex justify-content-center">
          <HeaderLinks setHoverItem={(name, categories) => { // Adjust this to handle categories
            setHoverItem(name);
            setCategories(categories); // Update categories based on the hovered item
          }} />
        </div>
      </nav>
      <motion.div
        className="expandable-menu"
        initial="hidden"
        animate={hoverItem ? "visible" : "hidden"}
        variants={expandVariants}
        style={{ backgroundColor: '#ffffff', width: '100%', position: 'fixed', top: '56px', zIndex: '10' }}
      >
        {/* Dynamically render categories based on the hovered item */}
        {categories.map(category => (
          <Link key={category.name} to={category.path} className="dropdown-item">
            {category.name}
          </Link>
        ))}
      </motion.div>
    </div>
  );
}
