import React, { useState } from 'react';
import { motion } from 'framer-motion';
import HeaderLinks from './HeaderLinks';
import Logo from './assets/mgilogo.png';
import { Link } from 'react-router-dom';

function Header() {
  const [hoverItem, setHoverItem] = useState(null);
  const [isExpanded, setIsExpanded] = useState(false);

  const categories = {
    Products: [
      { name: 'Electronics', path: '/products/electronics' },
      { name: 'Furniture', path: '/products/furniture' },
    ],
    // Define other categories for "About", "Contact", etc., as needed
  };

  const headerVariants = {
    initial: { height: "initial", backgroundColor: "transparent" },
    expanded: { height: "50vh", backgroundColor: "#ffffff", transition: { duration: 0.5 } },
  };

  return (
    <motion.div
      initial="initial"
      animate={isExpanded ? "expanded" : "initial"}
      variants={headerVariants}
      className="header-container"
      onMouseLeave={() => setIsExpanded(false)}
    >
      <nav className="navbar navbar-expand-lg navbar-light fixed-top">
        <div className="container-fluid d-flex justify-content-center">
          <HeaderLinks setHoverItem={(item) => { setHoverItem(item); setIsExpanded(!!item); }} />
        </div>
      </nav>
      {isExpanded && hoverItem && categories[hoverItem] && (
        <div className="categories-container d-flex flex-column align-items-center">
          {categories[hoverItem].map((category, index) => (
            <Link key={index} to={category.path} className="category-link">
              {category.name}
            </Link>
          ))}
        </div>
      )}
    </motion.div>
  );
}
export default Header;
