import React from 'react';
import { Link } from 'react-router-dom';

function HeaderLinks({ setHoverItem, setCategories }) {
  const links = [
    { name: 'Home', path: '/', categories: [] },
    {
      name: 'Products',
      path: '/products',
      categories: [
        { name: 'Electronics', path: '/products/electronics' },
        { name: 'Furniture', path: '/products/furniture' },
      ],
    },
    { name: 'About', path: '/about', categories: [] },
    { name: 'Contact', path: '/contact', categories: [] },
  ];

  // Example of where to apply the custom class for category links
  return (
    <div className="navbar-nav">
      {links.map(({ name, path, categories }) => (
        <div
          key={name}
          onMouseEnter={() => { setHoverItem(name); setCategories(categories); }} // Updated to set categories on hover
          onMouseLeave={() => setHoverItem(null)}
          className="nav-item"
        >
          <Link to={path} className="nav-link">{name}</Link>
          {name === 'Products' && categories.length > 0 && (
            <div className="dropdown-menu show"> {/* Use Bootstrap or custom styling to show/hide */}
              {categories.map(category => (
                <Link key={category.name} to={category.path} className="dropdown-item custom-category-link">
                  {category.name}
                </Link>
              ))}
            </div>
          )}
        </div>
      ))}
    </div>
  );
}

export default HeaderLinks;
