import React, { useState, useEffect } from 'react';
import axios from 'axios';

const SignupForm = () => {
    const [email, setEmail] = useState('');
    const [password1, setPassword1] = useState('');
    const [password2, setPassword2] = useState('');
    const [userCount, setUserCount] = useState(0); // State to store user count

    useEffect(() => {
        // Fetch the user count on component mount
        axios.get('/api/user-count/')
            .then(response => {
                setUserCount(response.data.user_count); // Set user count
            })
            .catch(error => {
                console.error("Could not fetch user count", error);
            });
    }, []); // Empty dependency array means this effect runs once on mount

    const handleSubmit = (e) => {
        e.preventDefault();
        axios.post('/dj-rest-auth/registration/', {
            email,
            password1,
            password2
        })
        .then(response => {
            console.log("Signup successful", response.data);
            setUserCount(prevCount => prevCount + 1); // Optionally increment user count on successful signup
        })
        .catch(error => {
            console.error("Signup error", error.response.data);
        });
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Email" required />
                <input type="password" value={password1} onChange={(e) => setPassword1(e.target.value)} placeholder="Password" required />
                <input type="password" value={password2} onChange={(e) => setPassword2(e.target.value)} placeholder="Confirm Password" required />
                <button type="submit">Sign Up</button>
            </form>
            <p>Total Registered Users: {userCount}</p> {/* Display user count */}
        </div>
    );
};

export default SignupForm;
