import React, { useState } from 'react';
import { motion } from 'framer-motion';
import HeaderLinks from './HeaderLinks';
import Logo from './assets/mgilogo.png';
import { Link } from 'react-router-dom';

function Header() {
  const [hoverItem, setHoverItem] = useState(null);
  const [isExpanded, setIsExpanded] = useState(false); // State to manage header expansion

  // Motion variants for the expanding animation and color change
  const headerVariants = {
    initial: { height: "initial", backgroundColor: "transparent" },
    expanded: { height: "50vh", backgroundColor: "#ffffff", transition: { duration: 0.5 } },
  };

  return (
    <motion.div
      initial="initial"
      animate={isExpanded ? "expanded" : "initial"}
      variants={headerVariants}
      className="header-container"
      onMouseLeave={() => setIsExpanded(false)} // Collapse when not hovering over items
    >
      <nav className="navbar navbar-expand-lg navbar-light fixed-top">
        <div className="container-fluid">
          <Link to="/" className="navbar-brand">
            <img src={Logo} alt="Logo" style={{ height: '50px' }} />
          </Link>
          <HeaderLinks setHoverItem={(item) => { setHoverItem(item); setIsExpanded(!!item); }} />
        </div>
      </nav>
      {hoverItem && (
        <div className="category-container">
          {/* Render categories here based on hoverItem */}
          {/* Example: */}
          {hoverItem === 'Products' && (
            <ul>
              <li><Link to="/products/electronics">Electronics</Link></li>
              <li><Link to="/products/furniture">Furniture</Link></li>
            </ul>
          )}
        </div>
      )}
    </motion.div>
  );
}

export default Header;
