import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function HeaderLinks({ setHoverItem }) {
  const links = [
    { name: 'Home', path: '/', categories: [] },
    {
      name: 'Products',
      path: '/products',
      categories: [
        { name: 'Electronics', path: '/products/electronics' },
        { name: 'Furniture', path: '/products/furniture' },
      ],
    },
    { name: 'About', path: '/about', categories: [] },
    { name: 'Contact', path: '/contact', categories: [] },
  ];

  // State to manage which link is being hovered
  const [hoveredLink, setHoveredLink] = useState(null);

  return (
    <ul className="navbar-nav">
      {links.map((link) => (
        <li
          key={link.name}
          className={`nav-item ${link.categories.length ? 'dropdown' : ''}`}
          onMouseEnter={() => setHoveredLink(link.name)}
          onMouseLeave={() => setHoveredLink(null)}
        >
          <Link to={link.path} className="nav-link" aria-expanded={hoveredLink === link.name ? true : false}>
            {link.name}
          </Link>
          {link.categories.length > 0 && hoveredLink === link.name && (
            <ul className="dropdown-menu" style={{ display: 'block' }}>
              {link.categories.map((category) => (
                <li key={category.name}>
                  <Link to={category.path} className="dropdown-item">
                    {category.name}
                  </Link>
                </li>
              ))}
            </ul>
          )}
        </li>
      ))}
    </ul>
  );
}

export default HeaderLinks;
