import React, { useState } from 'react';
import { motion } from 'framer-motion';
import HeaderLinks from './HeaderLinks';
import Logo from './assets/mgilogo.png';
import { Link } from 'react-router-dom';

function Header() {
  const [hoverItem, setHoverItem] = useState(null);

  // Content for each section that could be expanded upon hover or click
  const contentMap = {
    Home: 'Home content here',
    Products: 'Products content here',
    About: 'About us content here',
    Contact: 'Contact us content here',
  };

  // Motion variants for the dropdown animation
  const dropdownVariants = {
    hidden: { height: 0, opacity: 0 },
    visible: { height: '50vh', opacity: 1, transition: { duration: 0.5 } },
  };

  return (
    <div>
      <nav className="navbar navbar-expand-lg navbar-light fixed-top" style={{ backgroundColor: hoverItem ? '#ffffff' : 'transparent' }}>
        <Link to="/" className="navbar-brand">
          <img src={Logo} alt="Logo" style={{ height: '50px' }} />
        </Link>
        <div className="container-fluid d-flex justify-content-center">
          <HeaderLinks setHoverItem={setHoverItem} />
        </div>
      </nav>
      <motion.div
        className="position-fixed w-100"
        initial="hidden"
        animate={hoverItem ? "visible" : "hidden"}
        variants={dropdownVariants}
        style={{ top: '64px', zIndex: 1 }} // Adjust top value based on your navbar's height
      >
        {/* Display dynamic content based on hoverItem */}
        <p className="p-4">{contentMap[hoverItem]}</p>
      </motion.div>
    </div>
  );
}

export default Header;
