import React from 'react';
import { Link } from 'react-router-dom';

function HeaderLinks({ setHoverItem, setCategories }) {
  const links = [
    { name: 'Home', path: '/', categories: [] },
    { 
      name: 'Products', 
      path: '/products',
      categories: [
        { name: 'Electronics', path: '/products/electronics' },
        { name: 'Furniture', path: '/products/furniture' },
      ] 
    },
    { name: 'About', path: '/about', categories: [] },
    { name: 'Contact', path: '/contact', categories: [] },
  ];

  return (
    <ul className="navbar-nav">
      {links.map(({ name, path, categories }) => (
        categories.length > 0 ? (
          // Dropdown for items with categories
          <li className="nav-item dropdown" key={name} onMouseLeave={() => setHoverItem(null)}>
            <a className="nav-link dropdown-toggle" href="#" id={`${name.toLowerCase()}-dropdown`} role="button" data-bs-toggle="dropdown" aria-expanded="false">
              {name}
            </a>
            <ul className="dropdown-menu" aria-labelledby={`${name.toLowerCase()}-dropdown`}>
              {categories.map(category => (
                <li key={category.name}>
                  <Link to={category.path} className="dropdown-item" onMouseEnter={() => setHoverItem(name, categories)}>
                    {category.name}
                  </Link>
                </li>
              ))}
            </ul>
          </li>
        ) : (
          // Regular link for items without categories
          <li className="nav-item" key={name}>
            <Link to={path} className="nav-link" onMouseEnter={() => setHoverItem(name, categories)}>
              {name}
            </Link>
          </li>
        )
      ))}
    </ul>
  );
}

export default HeaderLinks;
