import React, { useState } from 'react';
import { motion } from 'framer-motion';
import HeaderLinks from './HeaderLinks';
import Logo from './assets/mgilogo.png'; // Ensure the logo path is correct
import { Link } from 'react-router-dom';

function Header() {
  const [hoverItem, setHoverItem] = useState(null);

  const dropdownVariants = {
    hidden: { height: 0, opacity: 0 },
    visible: { height: '50vh', opacity: 1, transition: { duration: 0.5 } },
  };

  return (
    <div>
      <motion.nav
        initial={{ y: -250 }}
        animate={{ y: 0 }}
        transition={{ type: 'spring', stiffness: 120 }}
        className="navbar navbar-expand-lg navbar-light bg-primary fixed-top"
      >
        <div className="container-fluid d-flex justify-content-between">
          <Link to="/" className="navbar-brand d-flex align-items-center">
            <img src={Logo} alt="Logo" style={{ height: '50px', marginRight: '10px' }} />
            <h1 className="mb-0" style={{ fontSize: '1.25rem' }}>My Website</h1>
          </Link>
          <HeaderLinks setHoverItem={setHoverItem} />
        </div>
      </motion.nav>
      <motion.div
        className="dropdown-content"
        initial="hidden"
        animate={hoverItem ? "visible" : "hidden"}
        variants={dropdownVariants}
        style={{ position: 'fixed', top: 0, width: '100%', backgroundColor: '#fff' }}
      >
        {/* Dynamic content based on hoverItem */}
        {hoverItem && <p>{hoverItem} content</p>}
      </motion.div>
    </div>
  );
}

export default Header;
