import React, { useState } from 'react';
import { motion } from 'framer-motion';
import HeaderLinks from './HeaderLinks';
import Logo from './assets/mgilogo.png';
import { Link } from 'react-router-dom';

function Header() {
  const [hoverItem, setHoverItem] = useState(null);

  return (
    <nav className="navbar navbar-expand-lg navbar-light fixed-top" 
         style={{ backgroundColor: hoverItem ? '#ffffff' : 'transparent', transition: 'background-color 0.3s ease' }}>
      <Link to="/" className="navbar-brand">
        {/* Add marginLeft style to the img tag to push the logo to the right */}
        <img src={Logo} alt="Logo" style={{ height: '50px', marginLeft: '10px' }} />
      </Link>
      <div className="container-fluid d-flex justify-content-center">
        <HeaderLinks setHoverItem={setHoverItem} />
      </div>
    </nav>
  );
}

export default Header;
