import React, { useState } from 'react';
import { motion } from 'framer-motion';
import HeaderLinks from './HeaderLinks';
import Logo from './assets/mgilogo.png';
import { Link } from 'react-router-dom';

function Header() {
  const [hoverItem, setHoverItem] = useState(null);

  // Define motion variants for the expanding animation
  const expandVariants = {
    hidden: { height: 0 },
    visible: { 
      height: '50vh', // Adjust as needed to control expanded size
      transition: { duration: 0.5 }
    },
  };

  return (
    <div>
      <nav className="navbar navbar-expand-lg navbar-light fixed-top" 
           style={{ backgroundColor: hoverItem ? '#ffffff' : 'transparent' }}>
        <Link to="/" className="navbar-brand">
          <img src={Logo} alt="Logo" style={{ height: '50px' }} />
        </Link>
        <div className="container-fluid d-flex justify-content-center">
          <HeaderLinks setHoverItem={setHoverItem} />
        </div>
      </nav>
      <motion.div
        className="expandable-menu"
        initial="hidden"
        animate={hoverItem ? "visible" : "hidden"}
        variants={expandVariants}
        style={{ backgroundColor: '#ffffff', width: '100%', position: 'fixed', top: '56px', zIndex: '10' }} // Adjust 'top' based on your navbar's height
      >
        {/* Placeholder for dynamic content based on hoverItem */}
        {hoverItem && <p style={{ textAlign: 'center', marginTop: '20px' }}>{`${hoverItem} content`}</p>}
      </motion.div>
    </div>
  );
}

export default Header;
